def fibonacci(n):
    fib_list = [0, 1]
    if n <= 1:
        return [fib_list[i] for i in range(n+1)]
    else:
        while fib_list[-1] < n:
            fib_list.append(fib_list[-1] + fib_list[-2])
        return fib_list[:-1]

print(fibonacci(10)) # [0, 1, 1, 2, 3, 5, 8]

def divisible_by_three(numbers):
    result = []
    for number in numbers:
        if number % 3 == 0:
            result.append(number)
    return result

input_list = [1, 2, 3, 4, 5, 6, 7, 8, 9]
print(divisible_by_three(input_list)) 
# Output: [3, 6, 9]

def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**(1/2))+1):
        if n%i == 0:
            return False
    return True

n = 7
if is_prime(n):
    print(f"{n} - простое число")
else:
    print(f"{n} - составное число")

